<script lang="ts">
  import { onDestroy } from "svelte";
  import { connectSnap } from "./metamask";
  import { clearState, snapState } from "./state";

  let _snapAvailable = false;
  let isConnecting = false;
  const isDevEnv = (import.meta as any).env.DEV;
  const snapConnectionSub = snapState.subscribe(state => _snapAvailable = state.snapAvailable);

  const connectHandler = async () => {
    isConnecting = true;
    await connectSnap();
    isConnecting = false;
  }

  // todo: add disconnect logic
  const disconnectHandler = () => clearState();

  onDestroy(() => snapConnectionSub());
</script>

